package com.nt.thread;

import java.util.Scanner;

public class SumOfTwoNo {
	static void m1() {
		int n=3;
		int	f=25,sum=0;
		
		for(int i=0;i<n;i++) {
			sum=sum+f;
		}
		System.out.println("sum is::"+sum);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		m1();

	}

}
